mapper.select:
SELECT t.id, t.case_name, t.case_type, t.case_charger, t.detail, ( CASE t.case_type WHEN 0 THEN '金融诈骗案件' WHEN 1 THEN '金融洗钱案件' WHEN 2 THEN '贪污贿赂案件' WHEN 3 THEN '贪污贿赂案件' WHEN 4 THEN '贪污贿赂案件' WHEN 5 THEN '贪污贿赂案件' END ) caseTypeName FROM case_info t
INSTR(t.case_name,#{caseName}) > 0
t.case_charger = #{caseCharger}
t.create_user = #{createUser}
