mapper.select:
SELECT
	t.name1, t.name2, t.money FROM Temp_Draw t

	LIMIT #{index}


	, #{offset}

select * from temp_draw aa inner join (
SELECT
t.name1,t.name2
FROM
temp_draw t

	
		AND t.time >= #{startTime}
	
	
		AND t.time <= #{endTime}
	
	
		AND t.money >= #{amount}
	

GROUP BY
t.name1,t.name2

	HAVING COUNT(t.card2) >= #{frequency}


	UNION
	SELECT
	tt.name1,tt.name2
	FROM
	temp_draw tt
	
		
			AND tt.time >= #{startTime}
		
		
			AND tt.time <= #{endTime}
		
	
	GROUP BY
	tt.name1,tt.name2,DATE_FORMAT(tt.time,'%Y-%c-%e')
	HAVING 1=1
	
		AND COUNT(tt.card2) >= #{everyDayFrequency}
	
	
		AND SUM(tt.money) > #{everyDayAmount}
	

) bb on (aa.name1 = bb.name1 and aa.name2 = bb.name2)
