mapper.select:
SELECT
	t.name1, t.name2, t.money FROM Temp_Draw t

	LIMIT #{index}


	, #{offset}

SELECT t.name1, t.name2, t.money, t.card2 FROM temp_draw t WHERE 1=1
t.money >= #{amount}
AND t.time >= #{startTime}
AND t.time <= #{endTime}
GROUP BY t.name1, t.name2, t.money, t.card2 HAVING COUNT( t.card2 ) >= #{frequency}
